mapper.select:
SELECT 
FROM CV.XCVRG
WHERE REPLACE_AGE_CODE = #{replaceAgeCode}
  AND RATE_SEX = #{rateSex}
  AND RATE_AGE = #{rateAge}
  AND RATE_SUB_AGE = #{rateSubAge}

SELECT COUNT(1)
FROM CV.XCVRG
WHERE REPLACE_AGE_CODE = #{replaceAgeCode}
  AND RATE_SEX = #{rateSex}
  AND RATE_AGE = #{rateAge}
  AND RATE_SUB_AGE = #{rateSubAge}

mapper.insert:
INSERT INTO CV.XCVRG (
    REPLACE_AGE_CODE, RATE_SEX, RATE_AGE, RATE_SUB_AGE,
    RATE_AGE_CHG_IND
) VALUES (
    #{replaceAgeCode}, #{rateSex}, #{rateAge}, #{rateSubAge},
    #{rateAgeChgInd}
)

mapper.delete:
DELETE FROM CV.XCVRG
WHERE REPLACE_AGE_CODE = #{replaceAgeCode}
  AND RATE_SEX = #{rateSex}
  AND RATE_AGE = #{rateAge}
  AND RATE_SUB_AGE = #{rateSubAge}
